// Importa os hooks e componentes necessários
import { useParams } from "react-router-dom";
import { useTasks } from "../context/TaskContext";
import { CheckCircle, Clock, AlertCircle, ArrowLeft } from "lucide-react";
import { Link } from "react-router-dom";
import "../App.css";

export default function TaskDetails() {
  const { id } = useParams();
  const { tasks } = useTasks();
  const task = tasks.find((t) => t.id.toString() === id);

  if (!task)
    return (
      <div className="error-container">
        <div className="error-content">
          <AlertCircle size={64} className="icon-error" />
          <h1>Tarefa não encontrada!</h1>
          <Link to="/" className="return-button">
            <ArrowLeft size={18} />
            Voltar para lista de tarefas
          </Link>
        </div>
      </div>
    );

  return (
    <div className="task-details-container">
      <div className="task-card">
        <Link to="/" className="return-button">
          <ArrowLeft size={18} />
          Voltar
        </Link>

        <div className="task-header">
          <h1>{task.title}</h1>
          <div className={`status-badge ${task.completed ? "completed" : "pending"}`}>
            {task.completed ? (
              <>
                <CheckCircle size={20} />
                Concluída
              </>
            ) : (
              <>
                <Clock size={20} />
                Pendente
              </>
            )}
          </div>
        </div>

        <div className="task-content">
          <div className="observation-box">
            <h3>Observações</h3>
            <p>{task.observation || <em>Nenhuma observação adicionada</em>}</p>
          </div>

          <div className="task-meta">
            <div className="meta-item">
              <span>ID da Tarefa:</span>
              <code>{task.id}</code>
            </div>
            <div className="meta-item">
              <span>Criada em:</span>
              {new Date(task.createdAt).toLocaleDateString()}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
